    private void tblContenidoMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblContenidoMouseClicked
        // TODO add your handling code here:
        if(this.tblContenido.getSelectedRow()!=-1){
            //QueryArticulos modelo = new QueryArticulos();//clase donde estan los querys
            
            this.txtArtId.setText(tblContenido.getValueAt(tblContenido.getSelectedRow(), 0).toString()); 
            this.txtArticulo.setText(tblContenido.getValueAt(tblContenido.getSelectedRow(), 1).toString()); 
            this.txtArtCod.setText(tblContenido.getValueAt(tblContenido.getSelectedRow(), 2).toString()); 
            // asignar valor al combo box con el valor en txtTipo
            this.txtArtDes.setText(tblContenido.getValueAt(tblContenido.getSelectedRow(), 3).toString()); 
            this.txtArtCost.setText(tblContenido.getValueAt(tblContenido.getSelectedRow(), 4).toString()); 
            
            this.btnArtGuardar.setEnabled(false);
            this.btnArtModificar.setEnabled(true);
            this.btnArtEliminar.setEnabled(true);        
            this.btnArtBuscar.doClick();
        }    
    }//GEN-LAST:event_tblContenidoMouseClicked

