    private void tblContenidoMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblContenidoMouseClicked
        // TODO add your handling code here:
        if(this.tblContenido.getSelectedRow()!=-1){
            //QueryClientes modelo = new QueryClientes();//clase donde estan los querys
            
            this.txtCliId.setText(tblContenido.getValueAt(tblContenido.getSelectedRow(), 0).toString()); 
            this.txtCliente.setText(tblContenido.getValueAt(tblContenido.getSelectedRow(), 1).toString()); 
            this.txtCliCor.setText(tblContenido.getValueAt(tblContenido.getSelectedRow(), 2).toString()); 
            // asignar valor al combo box con el valor en txtTipo
            this.txtCliDir.setText(tblContenido.getValueAt(tblContenido.getSelectedRow(), 3).toString()); 
            this.txtCliCod.setText(tblContenido.getValueAt(tblContenido.getSelectedRow(), 4).toString()); 
            this.txtCliTel.setText(tblContenido.getValueAt(tblContenido.getSelectedRow(), 5).toString()); 
            
            this.btnCliGuardar.setEnabled(false);
            this.btnCliModificar.setEnabled(true);
            this.btnCliEliminar.setEnabled(true);        
            this.btnCliBuscar.doClick();
        }    
    }//GEN-LAST:event_tblContenidoMouseClicked

